INSERT INTO `luatgiaothong` (`id`, `HinhAnh`, `PhuongTien`, `TieuDe`, `NoiDung`, `MucPhat`, `NghiDinh`, `created_at`, `updated_at`) VALUES
(3, 'vv_1575043314.png', 'Xe máy', 'Xe máy: Không sang tên đổi chủ , không chính chủ bị phạt bao tiền?', 'Cá nhân , tổ chức là chủ xe mô tô, xe gắn máy và các loại xe tương tự xe mô tô: Không làm thủ tục đăng ký sang tên xe (để chuyển tên chủ xe trong Giấy đăng ký xe sang tên của mình) theo quy định khi mua, được cho, được tặng, được phân bổ', '100.000 Đ - 200.000 Đ đối với cá nhân - 200.000 Đ - 400.000 Đ đối với tổ chức', 'Theo: Nghị định 46/2016/NĐ-CP về xử phạt hành chính trong lĩnh vực giao thông', '2019-11-29 09:01:54', '2019-11-29 09:01:54'),
(4, 'vv_1575043456.png', 'Xe máy', 'Sử dụng thiết bị phát tín hiệu trái quy định bị phạt bao tiền?', 'Người điều khiển xe xe mô tô, xe gắn máy (kể cả xe máy điện), các loại xe tương tự xe mô tô và các loại xe tương tự xe gắn máy.:', '80.000 Đ - 100.000 Đ', 'Theo: Nghị định 46/2016/NĐ-CP về xử phạt hành chính trong lĩnh vực giao thông', '2019-11-29 09:04:16', '2019-11-29 09:04:16'),
(5, 'vv_1575043522.png', 'Xe máy', 'Xe máy: Điều khiển xe bằng một bánh bị phạt bao tiền?', 'Mức phạt bổ sung: - Phạt tiền từ 10.000.000 đồng đến 14.000.000 đồng Nếu gây tai nạn giao thông hoặc không chấp hành hiệu lệnh dừng xe của người thi hành công vụ (Khoản 10 Điều 6) - Tước quyền sử dụng Giấy phép lái xe từ 02 tháng đến 04 tháng;', '5.000.000 Đ - 7.000.000 Đ', 'Theo: Nghị định 46/2016/NĐ-CP về xử phạt hành chính trong lĩnh vực giao thông', '2019-11-29 09:05:22', '2019-11-29 09:05:22'),
(6, 'vv_1575043618.png', 'Xe máy', 'Xe máy: Bấm còi trong thời gian từ 22 h đến 5 h bị phạt bao tiền?', 'Người điều khiển xe xe mô tô, xe gắn máy (kể cả xe máy điện), các loại xe tương tự xe mô tô và các loại xe tương tự xe gắn máy.: Bấm còi trong thời gian từ 22 giờ ngày hôm trước đến 05 giờ ngày hôm sau', '5000000', 'Theo: Nghị định 46/2016/NĐ-CP về xử phạt hành chính trong lĩnh vực giao thông', '2019-11-29 09:06:59', '2019-11-29 09:06:59');

